  if (!result.allowed) {
    throw new Error('アクセスが拒否されました: ' + result.reason);
  }

  console.log(
    `✅ ユーザーアクセス検証成功: ${User.email()} は ${requestUserId} のデータにアクセスできます。`
  );
  return result;
}

